import React, { useContext, useState } from 'react';
import proyectoContext from '../../context/proyectos/proyectoContext'
import tareaContext from '../../context/tareas/tareaContext'

const FormTarea = () => {

  // STATE de proyectos
  const proyectosContext = useContext(proyectoContext)
  const { proyecto } = proyectosContext

  // obtener la fn del context de tareas
  const tareasContext = useContext(tareaContext)
  const { agregarTarea } = tareasContext

  // STATE del formulario
  const [tarea, setTarea] = useState({
      nombre: ''
  })
  // extraer nombre de la tarea
  const { nombre } = tarea;

  // Si no hay ningún proyecto seleccionado
  if(!proyecto) return null;

  // Array destructuring para extraer proyecto actual
  const [ poryectoActual ] = proyecto;

  // Leer los valores del formulario
  const handleChange = e => {
      setTarea({
          ...tarea,
          [e.target.name] : e.target.value
      })
  }

  const onSubmit = e => {
    e.preventDefault();
    // Validar
    // pasar la validación
    // Agregar la nueva tarea al state.tareas, en su proyecto y con estado "incompleta"
    tarea.proyectoId = poryectoActual.id
    tarea.estado = false
    agregarTarea()
    // Reiniciar el form
  }

  return (  
      <div className='formulario'>
          <form
              onSubmit={onSubmit}
          >
              <div className='contenedor-input'>
                  <input
                      type='text'
                      className='input-text'
                      placeholder='Nombre tarea...'
                      name='nombre'
                      value={nombre}
                      onChange={handleChange}
                  />
              </div>
              <div className='contenedor-input'>
                  <input
                      type='submit'
                      className='btn btn-primario btn-submit btn-block'
                      value='Agregar tarea'
                  />
              </div>
          </form>
      </div> 
  );
}

export default FormTarea;
