import React, { Fragment, useContext } from 'react';
import proyectoContext from '../../context/proyectos/proyectoContext'
import Tarea from './Tarea'

const ListaTareas = () => {

    // Obtener el state de proyects
    const proyectosContext = useContext(proyectoContext)
    // extraemos del context, mediante el proyectoContext.provider
    const { proyecto, eliminarProyecto } = proyectosContext

    // Si no hay ningún proyecto seleccionado
    if(!proyecto) return <h2>Selecciona un proyecto</h2>

    // Array destructuring para extraer proyecto actual
    const [ poryectoActual ] = proyecto

    const tareasProyecto = [
        { nombre: 'Aprender useContext', estado: false },
        { nombre: 'Aprender useState', estado: true },
        { nombre: 'Aprender Redux', estado: false }
    ]  

    return (  
        <Fragment>
            <h2>Proyecto: {poryectoActual.nombre}</h2>
            <ul className='listado-tareas'>
                { tareasProyecto.length === 0
                  ? ( <li className='tarea'><p>No hay tareas</p></li> )
                  : tareasProyecto.map(tarea => (
                      <Tarea
                          tarea={tarea}
                      />
                  ))
                }
            </ul>

            <button
                type='button'
                className='btn btn-eliminar'
                onClick={() => eliminarProyecto(proyectoId)}
            >Eliminar proyecto &times;</button>
        </Fragment>
    );
}

export default ListaTareas;
