// El Reducer se encarga de hacer CAMBIOS EN EL STATE
import { FORMULARIO_PROYECTO } from '../../types'

export default (state, action) => {
  switch(action.type) {
    case FORMULARIO_PROYECTO:
        return{
            ...state,
            formulario: true
        }

    default: 
        return state;
  }
}
