import React from 'react';

const Tarea = ({tarea}) => {
  return (  
      <li className='tarea sombra'>
          <p>{tarea.nombre}</p>
          <div className='estado'>
              { tarea.estado 
                ?
                    (
                        <button
                            type='button'
                            className='completo'
                        >
                          Completada
                        </button>
                    )
                :
                    (
                        <button
                        type='button'
                        className='incompleto'
                        >
                          Pendiente
                        </button>
                    )

              }
          </div>
      </li>

  );
}

export default Tarea;
