import React, { useReducer } from 'react';
import tareaContext from './tareaContext'
import tareaReducer from './tareaReducer';

import { 
    TAREAS_PROYECTO,
    AGREGAR_TAREA 
} from '../../types'

const TareaState = props => {

    const initialState = {
        tareas: [
            { nombre: 'Aprender useContext', estado: false, proyectoId: 1 },
            { nombre: 'Aprender useState', estado: true, proyectoId: 2 },
            { nombre: 'Aprender Redux', estado: false, proyectoId: 3 },
            { nombre: 'Aprender Next.Js', estado: false, proyectoId: 3 },
            { nombre: 'Aprender useContext', estado: false, proyectoId: 2 },
            { nombre: 'Aprender useReducer', estado: false, proyectoId: 2 },
        ],
        tareasproyecto: null
    }

    // Crear dispatch y el state
    const [state, dispatch] = useReducer(tareaReducer, initialState)

    // FUNCIONES

    // Obtener las tareas de un proyecto
    const obtenerTareas = proyectoId => {
        dispatch({
            type: TAREAS_PROYECTO,
            payload: proyectoId
        })
    }

    // Agregar una tarea
    const agregarTarea = tarea => {
        dispatch({
            type: AGREGAR_TAREA,
            payload: tarea
        })
    }

    return(
        <tareaContext.Provider
            value={{
                tareas: state.tareas,
                tareasproyecto: state.tareasproyecto,
                obtenerTareas
            }}
        >
          {props.children}
        </tareaContext.Provider>   
    )
}

export default TareaState;
